import React, { Component } from 'react';

export default class SignIn extends Component {
    constructor(props){
        super(props);
        this.state = {
            email: "",
            password: "",
        };
        this.handleSubmit = this.handleSubmit.bind(this)
    }
    async handleSubmit(e) {
      e.preventDefault();
      const token = window.localStorage.getItem("token");
      
      if (token && token.length !== 0) {
          // Logout logic
          console.log("Logging out...");
          window.localStorage.removeItem("token");
          window.localStorage.removeItem("LoggedIn");
          // Redirect to the login page or home after logout
          window.location.href = "/login"; // Change this to the desired URL after logout
      } else {
          // Login logic
          const { email, password } = this.state;
          console.log(email, password);
          try {
              const response = await fetch("http://localhost:5000/loginuser", {
                  method: "POST",
                  crossDomain: true,
                  headers: {
                      "Content-Type": "application/json",
                      Accept: "application/json",
                      "Access-Control-Allow-Origin": "*",
                  },
                  body: JSON.stringify({
                      email,
                      password,
                  }),
              });
  
              const data = await response.json();
              console.log(data, "userLogged in");
  
              if (data.status === "ok") {
                  window.localStorage.setItem("LoggedIn", true);
                  window.localStorage.setItem("token", data.data);
                  alert("Login successful");
                  // Redirect to home or dashboard after successful login
                  window.location.href = "/"; // Change this to the desired URL after login
              } else {
                  // Handle login failure if needed
                  alert("Login failed");
              }
          } catch (error) {
              console.error('Error occurred during login:', error);
              // Handle error scenarios during login
              alert("Error occurred during login");
          }
      }
  }
  
